import React, { Component } from 'react';
import './Consultas.css';
import Modal from '../components/Modal/Modal';
import Backdrop from '../components/Backdrop/Backdrop';
import AuthContext from '../context/auth-context';

class ConsultasPage extends Component {
    state = {
        creating: false
    };

    constructor(props) { 
        super(props);
        this.titleElRef = React.createRef();
        this.medicoElRef = React.createRef();
        this.dateElRef = React.createRef();
        this.descriptionElRef = React.createRef();
    }

    componentDidMount() {
        this.fetchConsultas();
    }

    static contextType = AuthContext;

    startCreateConsultaHandler = () => {
        this.setState({creating: true});
    };

    modalConfirmHandler = () => {
        this.setState({creating: false});
        const title = this.titleElRef.current.value;
        const date = this.dateElRef.current.value;
        const description = this.descriptionElRef.current.value;

        if (
            title.trim().length === 0 || 
            date.trim().length === 0 || 
            description.trim().length === 0)
        {
            return;
        }

        const consulta = {title, date, description};

        console.log(consulta);

        
        const requestBody = {
            query: `
                mutation {
                    createConsulta(consultaInput: {title: "${title}", description: "${description}", date: "${date}"}) {
                        _id
                        title
                        description
                        date
                        creator {
                            _id
                            email
                        }
                    }
                }
            `
            };
        
        const token = this.context.token;

        fetch('http://localhost:8000/graphql', {
            method: 'POST',
            body: JSON.stringify(requestBody),
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer '+ token
            }
        })
        .then(res => {
            if(res.status !== 200 && res.status !== 201) {
                throw new Error('Authentication failed');
            }
            return res.json();
        })
        .then(resData => {
            console.log(resData);
        })
        .catch(err =>{
            console.log(err);
        });
    };

    modalCancelHandler = () => {
        this.setState({creating: false});
    };

    fetchConsultas() {
       
        const requestBody = {
            query: `
                query {
                    consultas {
                        _id
                        title
                        description
                        date
                        creator {
                            _id
                            email
                        }
                    }
                }
            `
            };
        
        const token = this.context.token;

        fetch('http://localhost:8000/graphql', {
            method: 'POST',
            body: JSON.stringify(requestBody),
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer '+ token
            }
        })
        .then(res => {
            if(res.status !== 200 && res.status !== 201) {
                throw new Error('Authentication failed');
            }
            return res.json();
        })
        .then(resData => {
            console.log(resData);
        })
        .catch(err =>{
            console.log(err);
        });
    };

    render() {
        return (
            <React.Fragment>
                {this.state.creating && <Backdrop/>}
                {this.state.creating && 
                <Modal 
                    title='Add Consulta' 
                    canCancel canConfirm 
                    onCancel={this.modalCancelHandler}
                    onConfirm={this.modalConfirmHandler}
                >
                    <form>
                        <div className="form-control">
                            <label htmlFor="title">Title</label>
                            <input type="text" id="title" ref={this.titleElRef}></input>
                        </div>
                        <div className="form-control">
                            <label htmlFor="medico">Medicos dispon√≠veis</label>
                            <select id="medico" ref={this.medicoElRef}></select>
                        </div>
                        <div className="form-control">
                            <label htmlFor="date">Date</label>
                            <input type="datetime-local" id="date" ref={this.dateElRef}></input>
                        </div>
                        <div className="form-control">
                            <label htmlFor="description">Description</label>
                            <textarea  id="description" rows="4" ref={this.descriptionElRef}></textarea>
                        </div>
                    </form>
                </Modal>}
                {this.context.token && <div className="consultas-control">
                    <p>Agende sua consulta!</p>
                    <button className="btn" onClick={this.startCreateConsultaHandler}>Criar consulta</button>
                </div>}
                <ul className="consultas__list">
                    <li className="consultas__list-item">asd</li>
                </ul>
            </React.Fragment>
        );
    }
}

export default ConsultasPage;
